import React from 'react';
import {Alert} from 'react-bootstrap';
import {PropTypes, classNames} from 'cs-web-components-externals';
import {prefixNS} from '../helpers';
import SVGIcon from './SVGIcon';

export default class InfoBox extends React.Component {

    constructor(props) {
        super(props);
    }

    render() {
        const alertType = this.props.alertType;
        const additionalClass = alertType === "info" ? prefixNS("InfoBox-info") :
                                alertType === "warning" ? prefixNS("InfoBox-warning") :
                                alertType === "danger" ? prefixNS("InfoBox-danger") : "";

        return (
            <Alert className={classNames(prefixNS('InfoBox'), additionalClass)}>
                <div className={prefixNS('InfoBox-inline-display')}>
                    {this.props.iconName ? <SVGIcon name={this.props.iconName} size="lg" /> :
                        this.props.iconSrc ? <SVGIcon src={this.props.iconSrc} size="lg" /> :
                        null
                    }
                </div>
                <div className={prefixNS('InfoBox-inline-display')}>
                    <div className="notification-title">
                        {this.props.title}
                    </div>
                    <div className={prefixNS('InfoBox-text')}>
                        {this.props.msg}
                    </div>
                </div>
            </Alert>
        );
    }
}

InfoBox.propTypes = {
    title: PropTypes.string.isRequired,
    msg: PropTypes.string.isRequired,
    alertType: PropTypes.oneOf([
        'info', 'warning', 'danger'
    ]).isRequired,
    iconName: PropTypes.string,
    iconSrc: PropTypes.string
};
