import React from 'react';
import {ProgressBar as RBProgressBar} from 'react-bootstrap';
import {PropTypes} from 'cs-web-components-externals';

/**
 * A ``ProcessBar`` shows progress in percent.
 *
 * +------------------+--------------+--------------+---------------------------------------------+
 * | Property         | Type         | Default      | Use                                         |
 * +==================+==============+==============+=============================================+
 * | value            | number       | -            | The progress value in percent               |
 * +------------------+--------------+--------------+---------------------------------------------+
 * | hideLabel        | bool         | -            | Hide the label text                         |
 * +------------------+--------------+--------------+---------------------------------------------+
 *
 */
export default class ProcessBar extends React.Component {
    render() {
        const {value, hideLabel, max} = this.props;
        const label = hideLabel ? undefined : `${Math.trunc((value * 100) / max)}%`;
        return (
            <RBProgressBar now={value} max={max} label={label}/>
        );
    }
}

ProcessBar.propTypes = {
    value: PropTypes.number,
    max: PropTypes.number,
    hideLabel: PropTypes.bool
};

ProcessBar.defaultProps = {
    hideLabel: false,
    max: 100
};
