/* eslint-env jest */

import React from 'react';
import {shallow} from 'enzyme';
import Button from '../Button.jsx';

describe('The TextButton component', () => {

    it('renders correct classes (see E042886)', () => {
        const btn = shallow(
            <Button.TextButton
                title="Button title"
                label="Push me!"
                className="my-special-class"
            />
        );
        expect(btn.hasClass('my-special-class')).toBe(true);
        expect(btn.hasClass('cs-web-components-base-text-button')).toBe(true);
        expect(btn.hasClass('cs-web-components-base-semantic-button-outline')).toBe(true);
    });

});
