import React from 'react';
import {ImmutablePropTypes} from 'cs-web-components-externals';
import DisplayContextLoader from '../DisplayContextLoader.jsx';
import ContentBlock from '../ContentBlock';
import {prefixNS} from '../../helpers';
import {formatStr} from '../../i18n';
import AttributeList from './AttributeList.jsx';

/**
 * This component displays attributes of a given contextObject. It retrieves its
 * data from the DisplayContext ``attributes``.
 *
 * React Properties
 * ----------------
 *
 * - contextObject: The object whose data should be displayed
 */
export default function Attributes({contextObject}) {
    return (
        <ContentBlock
            className={prefixNS('attributes')}
            collapsible={true}
            expanded={true}
            title={formatStr('attributes_panel_title')}
        >
            <ContentBlock.Body>
                <DisplayContextLoader
                    displayContext='attributes'
                    contextObject={contextObject}
                >
                    <AttributeList />
                </DisplayContextLoader>
            </ContentBlock.Body>
        </ContentBlock>
    );
}
Attributes.propTypes = {
    contextObject: ImmutablePropTypes.map
};
