import React from 'react';
import {PropTypes} from 'cs-web-components-externals';

/**
 * FACC helper component to manage the expanded state of the wrapped child
 */
export default class ExpandedStateHelper extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            expanded: props.initialExpanded
        };
        this.onExpanded = this.onExpanded.bind(this);
    }

    onExpanded(expanded) {
        this.setState({expanded: expanded});
    }

    render() {
        return this.props.children(this.state.expanded, this.onExpanded);
    }
}
ExpandedStateHelper.propTypes = {
    initialExpanded: PropTypes.bool
};
ExpandedStateHelper.defaultProps = {
    initialExpanded: true
};
