import React from "react";
import ButtonGroup from '../ButtonGroup.jsx';
import {PropTypes} from 'cs-web-components-externals';

export default class FileQuickAccessMenu extends React.Component {

    constructor(props) {
        super(props);
        this.state = {menuItems: this.props.getMenuOperations()};
    }

    updateContext() {
        this.setState({menuItems: this.props.getMenuOperations()});
    }

    render() {
        return (
            <ButtonGroup>
                {this.state.menuItems}
                {this.props.dropdown}
            </ButtonGroup>
        );
    }
}

FileQuickAccessMenu.propTypes = {
    getMenuOperations: PropTypes.func.isRequired,
    dropdown: PropTypes.object.isRequired
};
