import React from 'react';
import {PropTypes, ImmutablePropTypes} from 'cs-web-components-externals';
import {prefixNS} from '../../helpers.js';
import Tiles from '../../tiles';

export default function UserPanel(props) {
    const {contextObject, fallBackImg} = props;
    const login = contextObject.get('login');
    return (
        <div className={prefixNS('titlebar__userpanel')}>
            <Tiles.Tile
                contextObject={contextObject}
                fallbackImg={fallBackImg}
                headerAttr='name'>
                {login}
            </Tiles.Tile>
        </div>
    );
}
UserPanel.propTypes = {
    contextObject: ImmutablePropTypes.map.isRequired,
    fallBackImg: PropTypes.string
};
