/**
 * Actions for interacting with the Related Objects Backend API defined in configurable_ui.py
 */

export const FETCH_RELATED_OBJECT_TILES = `${componentNameSpace}-FETCH_RELATED_OBJECT_TILES`;
export const RELATED_OBJECT_TILES_FETCHED = `${componentNameSpace}-RELATED_OBJECT_TILES_FETCHED`;

export function fetchRelatedObjectTiles(classname, id) {
    return {
        type: FETCH_RELATED_OBJECT_TILES,
        payload: {classname, id},
    };
}

export function relatedObjectTilesFetched(classname, relshipConfigs) {
    return {
        type: RELATED_OBJECT_TILES_FETCHED,
        payload: {classname, relshipConfigs},
    };
}
