import Immutable from 'immutable';
import {Registry} from '../../../registry.js';
import {RELATED_OBJECT_TILES_FETCHED} from '../actions/related-objects.js';

function relatedObjectTiles(state = Immutable.Map(), action) {
    switch (action.type) {
        case RELATED_OBJECT_TILES_FETCHED:
            return state.set(action.payload.classname, action.payload.relshipConfigs);
        default:
            return state;
    }
}

Registry.registerReducers({relatedObjectTiles});
