import React from 'react';
import {getAppSetup, prefixNS} from '../helpers';

export default class EmbeddedPage extends React.Component {
    shouldComponentUpdate() {
        // <iframe> should not be rerendered
        return false;
    }

    render() {
        const compName = prefixNS('embedded_page');
        const src = getAppSetup().getIn([compName, 'link']);
        return (
            <iframe src={src} className={compName}/>
        );
    }
}
