import React from 'react';
import PageFrame from '../components/pageframe';
import {getAppSetup, prefixNS} from '../helpers.js';

const ErrorPage = props => {
    const appSetup = getAppSetup();
    return (
        <PageFrame.ApplicationFrame>
            <div className={prefixNS('error-text')}>
                <h1>
                    Server Status Code {appSetup.getIn(['server_error', 'code'])}
                    - {appSetup.getIn(['server_error', 'title'])}
                </h1>
                <p>{appSetup.getIn(['server_error', 'explanation'])}</p>
            </div>
            {props.children}
        </PageFrame.ApplicationFrame>
    );
};

export default ErrorPage;
