import React from 'react';
import {ImmutablePropTypes} from 'cs-web-components-externals';
import {prefixNS} from '../helpers.js';

export default function DetailLayout(props) {
    if (props.contextObject === null || props.contextObject === undefined) {
        // Should be handled at a higher level, make sure that nothing breaks
        // here if that fails for some reason.
        return null;
    }
    return (
        <div className={prefixNS('detail-layout')}>
            {props.children}
        </div>
    );
}
DetailLayout.propTypes = {
    contextObject: ImmutablePropTypes.map
};
