import React from 'react';
import {PropTypes} from 'cs-web-components-externals';
import {formatStr} from '../i18n.js';
import Button from '../components/Button.jsx';

export default function MinifyButton(props) {
    const {minified, toggleMinified} = props;
    const iconName = minified ? 'csweb_menu_down' : 'csweb_menu_up';
    const title = minified ? 'object_header_expand' : 'object_header_collapse';
    return (
        <Button.IconButton
            onClick={toggleMinified}
            iconName={iconName}
            size="sm"
            buttonStyle="auxiliary"
            title={formatStr(title)} />

    );
}
MinifyButton.propTypes = {
    minified: PropTypes.bool.isRequired,
    toggleMinified: PropTypes.func.isRequired
};
