import React from 'react';
import Dialog from './Dialog.jsx';
import {ListGroup, ListGroupItem} from 'react-bootstrap';
import {PropTypes} from 'cs-web-components-externals';

/**
 * @name SingleSelection
 * @class
 * @memberof module:Dialog
 * @extends React.Component
 */
export function SingleSelection(props) {
    const {children, ...other} = props;
    return (
        <Dialog {...other}
                hideFooter={true}>
            {children}
        </Dialog>
    );
}

/**
 * @name SingleListSelection
 * @class
 * @memberof module:Dialog
 * @extends React.Component
 */
export class SingleListSelection extends React.Component {
    render() {
        const {children, onItemSelected, ...others} = this.props;
        return (
            <SingleSelection {...others}
                             size={Dialog.SIZE_SMALL}
                             onHide={() => {onItemSelected(undefined)}}>
                <ListGroup>
                    {children.map((item, index) => (
                         <ListGroupItem key={index} onClick={() => {onItemSelected(index)}}>
                             {item}
                         </ListGroupItem>))}
                </ListGroup>
            </SingleSelection>
        );
    }
}

SingleListSelection.propTypes = {
    onItemSelected: PropTypes.func
};
