import {DATA_TYPES} from '../constants.js';

const TEXT = 0;
const INTEGER = 1;
const FLOAT = 2;
const DATE = 3;
export default {
    TEXT,
    INTEGER,
    FLOAT,
    DATE
};

/* Converts a form data type to a standard data type */
export function toDATA_TYPE(t) {
    switch (t) {
        case TEXT: return DATA_TYPES.CHAR;
        case INTEGER: return DATA_TYPES.INTEGER;
        case FLOAT: return DATA_TYPES.FLOAT;
        case DATE: return DATA_TYPES.DATE;
    }
    return DATA_TYPES.CHAR;
}
