import React from 'react';
import {PropTypes} from 'cs-web-components-externals';
import {formatStr} from '../../i18n.js';
import Dialog from '../../dialogs';

export default function CancelQuery(props) {
    return (
        <Dialog.YesNo title={formatStr('cancel_operation_title')}
                      onConfirm={yes => {yes ? props.forceCancel() : props.cancelQuery()}}
                      size={Dialog.SIZE_SMALL}
                      show={props.show}>
            {formatStr('cancel_operation_query')}
        </Dialog.YesNo>
    );
}
CancelQuery.propTypes = {
    show: PropTypes.bool,
    forceCancel: PropTypes.func.isRequired,
    cancelQuery: PropTypes.func.isRequired
};
CancelQuery.defaultProps = {
    show: true
};
