import {Registry} from '../registry.js';
import formWithOperations from './reducers/form_with_operations.js';
import ConfiguredForm from './containers/ConfiguredForm.jsx';
import FormWithOperations from './containers/FormWithOperations.jsx';
import FormControlWrapper from './components/FormControlWrapper.jsx';
import DisplayForm from './containers/DisplayForm.jsx';
import OperationHandler, {
    WithOperationHandler, applyExtension, composeExtensions
} from './containers/OperationHandler.jsx';
import OperationTrigger, {WithOperationTrigger} from './containers/OperationTrigger.jsx';
import OperationScope from './containers/OperationScope.jsx';
import OperationMessageBox from './components/OperationMessageBoxInProvider.jsx';
import Form, {FormRaw} from './containers/Form.jsx';

Registry.registerComponent(`${componentNameSpace}-OperationScope`, OperationScope);
Registry.registerComponent(`${componentNameSpace}-OperationMessageBox`, OperationMessageBox);
Registry.registerComponent(`${componentNameSpace}-ConfiguredForm`, ConfiguredForm);
Registry.registerComponent(`${componentNameSpace}-FormWithOperations`, FormWithOperations);
Registry.registerComponent(`${componentNameSpace}-FormControlWrapper`, FormControlWrapper);
Registry.registerComponent(`${componentNameSpace}-OperationHandler`, OperationHandler);
Registry.registerComponent(`${componentNameSpace}-OperationTrigger`, OperationTrigger);
Registry.registerComponent(`${componentNameSpace}-Form`, Form);

Registry.registerReducer('formWithOperations', formWithOperations);

export {
    Form, FormRaw,
    DisplayForm,
    OperationMessageBox,
    OperationTrigger,
    WithOperationTrigger,
    OperationHandler,
    WithOperationHandler,
    OperationScope,
    ConfiguredForm,
    FormWithOperations,
    applyExtension,
    composeExtensions
};
