import React from 'react';
import {PropTypes, classNames} from 'cs-web-components-externals';
import {FormGroup} from 'react-bootstrap';
import {TextButton} from '../components/Button.jsx';
import {sharedDefaultProps} from './sharedPropTypes.js';
import {prefixNS} from '../helpers.js';

/**
 * Form control button component
 *
 * +------------------+--------------+--------------+---------------------------------------------+
 * | Property         | Type         | Default      | Use                                         |
 * +==================+==============+==============+=============================================+
 * | label            | string       | -            | Button text                                 |
 * +------------------+--------------+--------------+---------------------------------------------+
 * | tooltip          | string       | -            | Button tooltip                              |
 * +------------------+--------------+--------------+---------------------------------------------+
 * | onClick          | func         | -            | Callback for button click event             |
 * +------------------+--------------+--------------+---------------------------------------------+
 *
 */
export default class Button extends React.Component {
    render() {
        const {label, tooltip, className, ...others} = this.props;
        const _className = classNames(
            prefixNS('form-input'),
            className
        );
        return (
            <FormGroup>
                <div className='form-input-button'>
                    <TextButton
                        title={tooltip}
                        label={label}
                        {...others}
                        className={_className} />
                </div>
            </FormGroup>
        );
    }
}

Button.propTypes = {
    className: PropTypes.string,
    label: PropTypes.string,
    tooltip: PropTypes.string,
    onClick: PropTypes.func,
};

Button.defaultProps = sharedDefaultProps;
