import React from 'react';
import {PropTypes} from 'cs-web-components-externals';
import ButtonToolbar from '../components/ButtonToolbar.jsx';
import Button from '../components/Button';
import Immutable from 'immutable';
import {prefixNS} from '../helpers';
/**
 *
 * This component shows a list of buttons in proper layout suits a form:
 *
 * +---------------+----------------+---------+------------------------------------------------------------------------+
 * | Property      | Type           | Default | Use                                                                    |
 * +===============+================+=========+========================================================================+
 * | buttons       | Immutable.List | -       | List of definitions for buttons, contains properties for each button:  |
 * |               |                |         | at least ``label`` for the button label and ``onClick`` event handler  |
 * +---------------+----------------+---------+------------------------------------------------------------------------+
 *
 * @module
 */
export default function FormActions(props) {
    const {buttons, ...others} = props;
    return (
        <div>
            <ButtonToolbar data-ce-id="form-action-toolbar" className={prefixNS("form-actions")}>
                {buttons.map((button, idx) => (
                    <Button.TextButton key={idx}
                                       onClick={button.get('onClick')}
                                       title={button.get('label')}
                                       disabled={button.get('disabled', false)}
                                       data-ce-id={'type-' + button.get('button_type')}
                                       buttonStyle={
                                           button.get('button_type') === 0 ? 'primary' : 'outline'}
                                      {...others}/>
                ))}
            </ButtonToolbar>
            <div className="clearfix"></div>
            </div>
    );
}

FormActions.propTypes = {
    buttons: PropTypes.instanceOf(Immutable.List)
};
