import React from 'react';
import TextInput from './TextInput';
import {sharedTextInputProps} from './sharedPropTypes';

/**
 * This component can be used to enter password. The user inputs would be masked. It takes such
 * properties just as the <:ref:`cs-web-components-base-formcontrols.TextInput.__default__`>.
 *
 * @module Password
 */
export default function Password(props) {
    return (
        <TextInput type="password" {...props} />
    );
}

Password.propTypes = {...sharedTextInputProps};
