import React from 'react';
import {FormGroup, Radio as _Radio} from 'react-bootstrap';
import {sharedCheckboxProps} from './sharedPropTypes';

/**
 * A ``Radio`` can be set in 2 states: ``checked`` or ``unchecked``. It has one more property
 * in addition to those defined in :ref:`form_control_common_props`:
 *
 * +------------------+--------------+--------------+---------------------------------------------+
 * | Property         | Type         | Default      | Use                                         |
 * +==================+==============+==============+=============================================+
 * | checked          | bool         | -            | Display the Radio as checked                |
 * +------------------+--------------+--------------+---------------------------------------------+
 *
 */
export default class Radio extends React.Component {
    render() {
        const {label, validationState, mandatory, mandatoryEmptyHint, ...others} = this.props;
        return (
            <FormGroup data-mandatory={mandatory}
                       data-mandatory-empty-hint={mandatoryEmptyHint}>
                <_Radio ref={ref => {this.input = ref}}
                        validationState={validationState}
                        {...others}>
                    <span title={label}>{label}</span>
                </_Radio>
            </FormGroup>
        );
    }
}

Radio.propTypes = {...sharedCheckboxProps};
