import Immutable from 'immutable';
import {PropTypes} from 'cs-web-components-externals';
import {prefixNS} from '../helpers.js';

export const sharedProps = {
    className: PropTypes.string,
    label: PropTypes.node,
    tooltip: PropTypes.string,
    readOnly: PropTypes.bool,
    onChange: PropTypes.func,
    name: PropTypes.string.isRequired,
    mandatory: PropTypes.bool,
    mandatoryEmptyHint: PropTypes.bool,
    validationState: PropTypes.string,
    afterLabelControl: PropTypes.node
};

export const sharedTextProps = {
    ...sharedProps,
    value: PropTypes.any
};

export const sharedTextInputProps = {
    ...sharedTextProps,
    compact: PropTypes.bool,
    beforeControl: PropTypes.node,
    afterControl: PropTypes.node
};

export const sharedCheckboxProps = {
    ...sharedProps,
    checked: PropTypes.bool
};

export const sharedComboBoxProps = {
    ...sharedTextInputProps,
    onSelect: PropTypes.func,
    textReadOnly: PropTypes.bool,
    onToggle: PropTypes.func,
    items: PropTypes.instanceOf(Immutable.List),
    itemComponentClass: PropTypes.node,
    emptyItemClass: PropTypes.node
};

export const sharedCatalogProps = {
    textReadOnly: PropTypes.bool,
    formData: PropTypes.instanceOf(Immutable.Map),
    operationState: PropTypes.instanceOf(Immutable.Map),
    contextClass: PropTypes.string,
    activeLanguage: PropTypes.string,
    selectURL: PropTypes.string,
    valueCheckURL: PropTypes.string,
    onCatalogChange: PropTypes.func
};

export const sharedDefaultProps = {
    className: prefixNS('form-input-default')
};
