import React from "react";
import {PropTypes} from 'cs-web-components-externals';

export default function FixedSidebar(props) {
    return (
        <div className={`${componentNameSpace}-fixed-sidebar`}>
            <div className={`${componentNameSpace}-fixed-sidebar__content`}>
                {props.content}
            </div>
            <div className={`${componentNameSpace}-fixed-sidebar__sidebar`}>
                {props.children}
            </div>
        </div>
    );
}

FixedSidebar.propTypes = {
    content: PropTypes.oneOfType([
        PropTypes.func,
        PropTypes.object
    ])
};
