import React from 'react';
import {PropTypes, classNames} from 'cs-web-components-externals';

export default class FlexBox extends React.Component {
    render() {
        const classes = classNames(
            'cs-web-components-base-flexbox',
            {'cs-web-components-base-flexbox-growfirstchild': this.props.growFirstChild}
        );
        const boxStyle = {
            flexDirection: this.props.vertical ? 'column' : 'row',
            flexWrap: this.props.wrap ? 'wrap' : 'nowrap'
        };
        return (
            <div className={classes} style={boxStyle}>
                {this.props.children}
            </div>
        );
    }
}

FlexBox.propTypes = {
    vertical: PropTypes.bool,
    wrap: PropTypes.bool,
    growFirstChild: PropTypes.bool
};
FlexBox.defaultProps = {
    vertical: false,
    wrap: true,
    growFirstChild: true
};
