import {Level} from './index.js';

export function getType(level) {
    switch (level) {
        case Level.EMPTY:
        case Level.WARNING:
            return "warning";
        case Level.PROGRESS:
        case Level.INFO:
            return "info";
        case Level.SUCCESS:
            return "success";
        case Level.ERROR:
            return 'danger';
    }
    return undefined;
}
