export const SET_COMPONENT_MODIFIED = `${componentNameSpace}-SET_COMPONENT_MODIFIED`;

export function setComponentModified(component, modified) {
    return {
        type: SET_COMPONENT_MODIFIED,
        payload: {component, modified}
    };
}
