import Immutable from 'immutable';
import {SET_COMPONENT_MODIFIED} from './actions.js';

export default function(state = Immutable.Map(), action) {
    switch (action.type) {
        case SET_COMPONENT_MODIFIED:
            return state.set(action.payload.component, action.payload.modified);
    }
    return state;
}
