import {ACTIONS} from '../constants.js';

/**
 * Spawn a saga for tab-handling with the given ``tabId``.
 * Use this id in ``navigateTabAsync`` and ``closeTabAsync``.
 */
export function openTabAsync(tabId) {
    return {
        type: ACTIONS.OPEN_TAB_ASYNC,
        payload: {tabId: tabId},
    };
}

/**
 * Navigate to ``url`` in window identified by ``tabId``.
 */
export function navigateTabAsync(tabId, url) {
    return {
        type: ACTIONS.NAVIGATE_TAB_ASYNC,
        payload: {tabId, url},
    };
}

/**
 * Close tab identified by ``tabId``.
 */
export function closeTabAsync(tabId) {
    return {
        type: ACTIONS.CLOSE_TAB_ASYNC,
        payload: {tabId},
    };
}
