import {Registry} from '../registry.js';

import setupOperationSagas from './sagas/operations.js';
import setupFormSagas from './sagas/form.js';
import setupFileSagas from './sagas/files.js';
import setupNavigateSagas from './sagas/navigate.js';
import setupHandlerSagas, {dispatchResultHandler} from './sagas/handlers.js';

import Handlers from './actions/handlers.js';
import {
    runOperation, addFiles, removeFiles
} from './actions/operations.js';

/* Setup sagas handling operation execution. */
Registry.registerSaga(setupOperationSagas);
Registry.registerSaga(setupFormSagas);
Registry.registerSaga(setupFileSagas);
Registry.registerSaga(setupNavigateSagas);
Registry.registerSaga(setupHandlerSagas);

/* Exports */
export default {
    Handlers,
    dispatchResultHandler,
    runOperation,
    addFiles,
    removeFiles,
};
