import {PropTypes} from 'cs-web-components-externals';

/**
 * Constants for use with overlays.
 *
 * These are exported as fields of
 * :ref:`cs-web-components-base-overlays.index.__module__`.
 * Use them like:
 *
 * .. code-block:: none
 *
 *    import {Overlays} from 'cs-web-components-base';
 *
 *    const {HorizontalAlignment} = Overlays;
 *
 * @module Overlay-Constants
 */

/**
 * An enumeration for specifying horizontal alignment for
 * aligned/layouted overlays. Values are
 *
 * - ``HorizontalAlignment.LEFT``
 * - ``HorizontalAlignment.RIGHT``
 */
export const HorizontalAlignment = {
    LEFT: 0,
    RIGHT: 1,
};

/**
 * PropType for HorizontalAlignment property.
 */
export const HorizontalAlignmentType = PropTypes.oneOf([
    HorizontalAlignment.LEFT,
    HorizontalAlignment.RIGHT,
]);

// Distance between parent component and overlay for AlignedOverlay layouting
export const ALIGNED_OVERLAY_DIST = 2;

/**
 * An enumeration for specifying vertical alignment for
 * aligned/layouted overlays. Values are
 *
 * - ``VerticalAlignment.BOTTOM``
 * - ``VerticalAlignment.TOP``
 */
export const VerticalAlignment = {
    BOTTOM: 0,
    TOP: 1,
};

/**
 * PropType for VerticalAlignment property.
 */
export const VerticalAlignmentType = PropTypes.oneOf([
    VerticalAlignment.BOTTOM,
    VerticalAlignment.TOP,
]);
