import Immutable from 'immutable';
import {SET_CONTEXT_OBJECTS} from '../actions/context-objects.js';

export default function contextObjects(state = Immutable.Map(), action) {
    switch (action.type) {
        case SET_CONTEXT_OBJECTS: {
            const path = action.payload.path.split('/');
            return state.setIn(path, action.payload.objectIds);
        }
        default:
            return state;
    }
}
