import Immutable from 'immutable';
import {
    USER_SETTING_FETCHED, USER_SETTING_RESET, USER_SETTING_STORED
} from '../actions/user-settings-actions';

export default function userSettings(state = Immutable.Map(), action) {
    switch (action.type) {
        case USER_SETTING_FETCHED:
        case USER_SETTING_STORED:
            return state.setIn(
                [action.payload.componentId, action.payload.settingKey],
                Immutable.fromJS(action.payload.value));
        case USER_SETTING_RESET:
            return state.deleteIn(
                [action.payload.componentId, action.payload.settingKey]
            );
        default:
            return state;
    }
}
