import React from 'react';
import {PropTypes} from 'cs-web-components-externals';
import ButtonToolbar from '../../components/ButtonToolbar.jsx';
import LinkControl from './Link';
import InlineControl from './Inline';
import BlockControl from './Block';
import FontControl from './Font';
import UndoRedoControl from './UndoRedo';

export default class StyleControlBar extends React.Component {
    render() {
        const {editorState, onChange, inlineStyles,
               blockStyles, additionalButtons, containerRef} = this.props;
        const additionalElements = additionalButtons.map((ele, i) =>
            React.cloneElement(ele, {
                editorState: editorState,
                onChange: onChange,
                key: i
            })
        );
        return (
            <ButtonToolbar>
                <UndoRedoControl editorState={editorState}
                                 onChange={onChange} />
                <FontControl editorState={editorState}
                             onChange={onChange}
                             containerRef={containerRef}/>
                <InlineControl editorState={editorState}
                               onChange={onChange}
                               styles={inlineStyles} />
                <BlockControl editorState={editorState}
                              onChange={onChange}
                              styles={blockStyles} />
                <LinkControl editorState={editorState}
                             onChange={onChange} />
                {additionalElements}
            </ButtonToolbar>
        );
    }
}

StyleControlBar.propTypes = {
    editorState: PropTypes.object.isRequired,
    onChange: PropTypes.func.isRequired,
    inlineStyles: PropTypes.array,
    blockStyles: PropTypes.array,
    additionalButtons: PropTypes.array,
    containerRef: PropTypes.object
};
