import React from 'react';
import {PropTypes} from 'cs-web-components-externals';
import Button from '../../components/Button.jsx';
import {formatStr} from '../../i18n';

export default class StyleControlButton extends React.Component {
    constructor() {
        super();
        this._onToggle = this._onToggle.bind(this);
    }

    _onToggle(e) {
        e.preventDefault();
        this.props.onToggle(this.props.style);
    }

    render() {
        const {active, iconName, style} = this.props;

        return (
            <Button.IconButton onMouseDown={this._onToggle}
                               active={active}
                               iconName={iconName}
                               buttonStyle="outline"
                               title={formatStr('richtext_' + style)}/>
        );
    }
}

StyleControlButton.propTypes = {
    onToggle: PropTypes.func.isRequired,
    iconName: PropTypes.string.isRequired,
    style: PropTypes.string.isRequired,
    active: PropTypes.bool
};
