import {ACTIONS} from '../constants.js';

export function loadFavouritesForClass(classname) {
    return {
        type: ACTIONS.LOAD_FAVOURITES_FOR_CLASS,
        payload: {classname}
    };
}

export function loadedFavouritesForClass(
    classname, favourites, defaultFavouriteId, classDesignation)
{
    return {
        type: ACTIONS.LOADED_FAVOURITES_FOR_CLASS,
        payload: {classname, favourites, defaultFavouriteId, classDesignation}
    };
}

export function loadAllFavourites() {
    return {
        type: ACTIONS.LOAD_ALL_FAVOURITES
    };
}

export function loadedAllFavourites(allFavourites)
{
    return {
        type: ACTIONS.LOADED_ALL_FAVOURITES,
        payload: {allFavourites}
    };
}

export function editedSearchFavourites(classname, oldFavourites, changedFavourites) {
    return {
        type: ACTIONS.EDITED_SEARCH_FAVOURITES,
        payload: {classname, oldFavourites, changedFavourites}
    };
}
