import {ACTIONS} from '../constants.js';
import {
    createSearchInstance, createCatalogSearchInstance, removeSearchInstance,
    selectFavourite, runSearch, resetSearch
} from './instances.js';

import {setSearchContext} from './context.js';

function initClassSearch(classname) {
    return {
        type: ACTIONS.INIT_CLASS_SEARCH,
        payload: {classname}
    };
}

/*
 * The action creators exported here are part of the API by which components can
 * use the class search functionality.
 */
export {
    initClassSearch,
    createSearchInstance,
    setSearchContext,
    createCatalogSearchInstance,
    removeSearchInstance,
    selectFavourite,
    runSearch,
    resetSearch
};
