import {ACTIONS} from '../constants.js';

export function loadSearchOperation(classname) {
    return {
        type: ACTIONS.LOAD_SEARCH_OP,
        payload: {classname}
    };
}

export function searchOperationLoaded(classname, opInfo, opData) {
    return {
        type: ACTIONS.SEARCH_OP_LOADED,
        payload: {classname, opInfo, opData}
    };
}

export function searchOperationLoadError(classname, error) {
    return {
        type: ACTIONS.SEARCH_OP_LOAD_ERROR,
        payload: {classname, error}
    };
}
