import Immutable from 'immutable';
import {ACTIONS} from '../constants.js';

export function context(state = Immutable.Map(), action) {
    switch (action.type) {
        case ACTIONS.SET_CONTEXT: {
            return state.merge(action.payload);
        }
        default:
            return state;
    }
}
