import {ReduxSaga} from 'cs-web-components-externals';
import {Registry} from '../../registry.js';
import operationSaga from './operation.js';
import favouritesSaga from './favourites.js';
import instancesSaga from './instances.js';
import classesSaga from './classes.js';

function* watcher() {
    yield ReduxSaga.effects.all([
        ReduxSaga.effects.call(operationSaga),
        ReduxSaga.effects.call(favouritesSaga),
        ReduxSaga.effects.call(instancesSaga),
        ReduxSaga.effects.call(classesSaga),
    ]);
}

Registry.registerSaga(watcher);
