export function getFavouriteResultLink(state, classname, favouriteId) {
    const {favouritesByClass} = state.search;
    return favouritesByClass.getIn([classname, 'favourites', favouriteId, 'resultLink']);
}

export function isSpecialFavourite(state, classname, favouriteId) {
    return (getFavouriteResultLink(state, classname, favouriteId) !== undefined);
}

export function getFavouriteName(state, classname, favouriteId) {
    const {favouritesByClass} = state.search;
    return favouritesByClass.getIn([classname, 'favourites', favouriteId, 'name']);
}
