import React from 'react';
import {storiesOf, knobs, centered} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {MenuItem} from 'react-bootstrap';
import {ButtonGroup, Button} from 'cs-web-components-base';
import {icons, colors} from './util.jsx';

storiesOf('ButtonGroup', {})
    .addDecorator(centered)
    .add('TextButton Group', () => (
        <ButtonGroup>
            <Button.TextButton
                label={knobs.text('label 1', 'TextButton 1')}
                buttonStyle={knobs.select("buttonStyle 1", colors, "outline")}/>
            <Button.TextButton
                label={knobs.text('label 2', 'TextButton 2')}
                buttonStyle={knobs.select("buttonStyle 2", colors, "outline")}/>
            <Button.TextButton
                label={knobs.text('label 3', 'TextButton 3')}
                buttonStyle={knobs.select("buttonStyle 3", colors, "outline")}/>
            <Button.DropdownTextButton
                label={knobs.text('label 4', 'DropdownTextButton')}
                buttonStyle={knobs.select("buttonStyle 4", colors, "outline")}
                id="DropdownTextButton">
                <MenuItem>MenuItem 1</MenuItem>
                <MenuItem>MenuItem 2</MenuItem>
                <MenuItem>MenuItem 3</MenuItem>
            </Button.DropdownTextButton>
        </ButtonGroup>
    ))
    .add('IconButton Group', () => (
        <ButtonGroup>
            <Button.IconButton
                iconName={knobs.select("iconName 1", icons, "csweb_favorite")}
                title="Grouped"
                buttonStyle={knobs.select("buttonStyle 1", colors, "outline")}/>
            <Button.IconButton
                iconName={knobs.select("iconName 2", icons, "csweb_check")}
                title="Icon"
                buttonStyle={knobs.select("buttonStyle 2", colors, "outline")}/>
            <Button.IconButton
                iconName={knobs.select("iconName 3", icons, "csweb_filter")}
                title="Buttons"
                buttonStyle={knobs.select("buttonStyle 3", colors, "outline")}/>
            <Button.DropdownIconButton
                iconName={knobs.select("iconName 4", icons, "csweb_add")}
                noCaret
                title="With Dropdown"
                buttonStyle={knobs.select("buttonStyle 4", colors, "outline")}
                id="DropdownTextButton">
                <MenuItem>MenuItem 1</MenuItem>
                <MenuItem>MenuItem 2</MenuItem>
                <MenuItem>MenuItem 3</MenuItem>
            </Button.DropdownIconButton>
        </ButtonGroup>
    )).add('Two IconButton Groups', () => (
        <div style={{display: 'flex'}}>
            <ButtonGroup>
                <Button.IconButton
                    iconName={knobs.select("iconName 1", icons, "csweb_favorite")}
                    title="Grouped"
                    buttonStyle={knobs.select("buttonStyle 1", colors, "outline")}/>
                <Button.IconButton
                    iconName={knobs.select("iconName 2", icons, "csweb_check")}
                    title="Icon"
                    buttonStyle={knobs.select("buttonStyle 2", colors, "outline")}/>
                <Button.IconButton
                    iconName={knobs.select("iconName 3", icons, "csweb_filter")}
                    title="Buttons"
                    buttonStyle={knobs.select("buttonStyle 3", colors, "outline")}/>
                <Button.DropdownIconButton
                    iconName={knobs.select("iconName 4", icons, "csweb_add")}
                    noCaret
                    title="With Dropdown"
                    buttonStyle={knobs.select("buttonStyle 4", colors, "outline")}
                    id="DropdownTextButton">
                    <MenuItem>MenuItem 1</MenuItem>
                    <MenuItem>MenuItem 2</MenuItem>
                    <MenuItem>MenuItem 3</MenuItem>
                </Button.DropdownIconButton>
            </ButtonGroup>
            <ButtonGroup>
                <Button.IconButton
                    iconName={knobs.select("iconName 1", icons, "csweb_favorite")}
                    title="Grouped"
                    buttonStyle={knobs.select("buttonStyle 1", colors, "outline")}/>
                <Button.IconButton
                    iconName={knobs.select("iconName 2", icons, "csweb_check")}
                    title="Icon"
                    buttonStyle={knobs.select("buttonStyle 2", colors, "outline")}/>
                <Button.IconButton
                    iconName={knobs.select("iconName 3", icons, "csweb_filter")}
                    title="Buttons"
                    buttonStyle={knobs.select("buttonStyle 3", colors, "outline")}/>
                <Button.DropdownIconButton
                    iconName={knobs.select("iconName 4", icons, "csweb_add")}
                    noCaret
                    title="With Dropdown"
                    buttonStyle={knobs.select("buttonStyle 4", colors, "outline")}
                    id="DropdownTextButton">
                    <MenuItem>MenuItem 1</MenuItem>
                    <MenuItem>MenuItem 2</MenuItem>
                    <MenuItem>MenuItem 3</MenuItem>
                </Button.DropdownIconButton>
            </ButtonGroup>
        </div>
    ));
