import React from 'react';
import {storiesOf, knobs, centered} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {MenuItem} from 'react-bootstrap';
import {ButtonToolbar, ButtonGroup, Button} from 'cs-web-components-base';
import {icons, colors} from './util.jsx';


storiesOf('ButtonToolbar', {})
    .addDecorator(centered)
    .add('TextButton Toolbar', () => (
        <ButtonToolbar>
            <ButtonGroup>
                <Button.TextButton
                    label={knobs.text('TextButton 1', 'TextButton 1')}
                    buttonStyle={knobs.select("buttonStyle 1", colors, "outline")}/>
                <Button.TextButton
                    label={knobs.text('TextButton 2', 'TextButton 2')}
                    buttonStyle={knobs.select("buttonStyle 2", colors, "outline")}/>
                <Button.TextButton
                    label={knobs.text('TextButton 3', 'TextButton 3')}
                    buttonStyle={knobs.select("buttonStyle 3", colors, "outline")}/>
            </ButtonGroup>
            <Button.TextButton
                label={knobs.text('TextButton 4', 'TextButton 4')}
                buttonStyle={knobs.select("buttonStyle 4", colors, "outline")}/>
            <Button.DropdownTextButton
                label={knobs.text('DropdownTextButton', 'DropdownTextButton')}
                buttonStyle={knobs.select("buttonStyle 5", colors, "outline")}
                id="DropdownTextButton">
                <MenuItem>MenuItem 1</MenuItem>
                <MenuItem>MenuItem 2</MenuItem>
                <MenuItem>MenuItem 3</MenuItem>
            </Button.DropdownTextButton>
        </ButtonToolbar>
    ))
    .add('IconButton Toolbar', () => (
        <ButtonToolbar>
            <ButtonGroup>
                <Button.IconButton
                    iconName={knobs.select("iconName 1", icons, "csweb_check")}
                    title="IconButton 1"
                    buttonStyle={knobs.select("buttonStyle 1", colors, "outline")}/>
                <Button.IconButton
                    iconName={knobs.select("iconName 2", icons, "csweb_history")}
                    title="IconButton 2"
                    buttonStyle={knobs.select("buttonStyle 2", colors, "outline")}/>
                <Button.IconButton
                    iconName={knobs.select("iconName 3", icons, "csweb_add")}
                    title="IconButton 3"
                    buttonStyle={knobs.select("buttonStyle 3", colors, "outline")}/>
            </ButtonGroup>
            <Button.IconButton
                iconName={knobs.select("iconName 4", icons, "csweb_edit")}
                title="IconButton 4"
                buttonStyle={knobs.select("buttonStyle 4", colors, "outline")}/>
            <Button.IconButton
                iconName={knobs.select("iconName 5", icons, "csweb_expand")}
                title="IconButton 5"
                buttonStyle={knobs.select("buttonStyle 5", colors, "outline")}/>
            <Button.IconButton
                iconName={knobs.select("iconName 6", icons, "csweb_history")}
                buttonStyle={knobs.select("buttonStyle 6", colors, "outline")}
                title="IconButton 6"/>
            <Button.DropdownIconButton
                iconName={knobs.select("iconName 7", icons, "csweb_information")}
                noCaret
                title="IconButton 7"
                buttonStyle={knobs.select("buttonStyle 7", colors, "outline")}
                id="DropdownIconButton">
                <MenuItem>MenuItem 1</MenuItem>
                <MenuItem>MenuItem 2</MenuItem>
                <MenuItem>MenuItem 3</MenuItem>
            </Button.DropdownIconButton>
        </ButtonToolbar>
    ))
;
