import React from 'react';
import {storiesOf, centered} from 'cs-web-components-storybook';
import {ConfiguredComponent, Registry} from 'cs-web-components-base';
import Immutable from 'immutable';


function ErrorComponent() {
    throw Error("A mighty error");
}
Registry.registerComponent('cs-dummy-package-FooBar', ErrorComponent);


const notFoundConf = Immutable.fromJS({
    name: 'cs-dummy-package-FluffyCuddles',
    components: {},
    properties: {}
});

const errorConf = Immutable.fromJS({
    name: 'cs-dummy-package-FooBar',
    components: {},
    properties: {}
});


storiesOf('ConfiguredComponent')
    .addDecorator(centered)
    .add('Not found', () => (
        <ConfiguredComponent configuration={notFoundConf}/>
    ))
    .add('Error', () => (
        <ConfiguredComponent configuration={errorConf}/>
    ))
;
