import React from 'react';
import {storiesOf, appContainer, centered} from 'cs-web-components-storybook';
import {FileDropzone} from 'cs-web-components-base';

class MyFiles extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            files: null
        };
        this.onDrop = this.onDrop.bind(this);
    }

    onDrop(files) {
        this.setState({
            files: files
        });
    }

    render() {
        return (
            <div>
                <FileDropzone onDrop={this.onDrop}>
                    <p>Drag and drop files here</p>
                </FileDropzone>
                {this.state && this.state.files ?
                    <FileDropzone.Preview files={this.state.files}/> : null
                }
            </div>
        );
    }
}


storiesOf('FileDropzone', {})
    .addDecorator(centered)
    .addDecorator(appContainer)
    .add('default', () => {
        return (
            <MyFiles />
        );
    });
