import React from 'react';
import {storiesOf, knobs, centered} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {IconAndLabel, SVGIconAndLabel, SVGIconAndLink, Icon} from 'cs-web-components-base';
import {icons} from './util.jsx';


const sizes = ['sm', 'md', 'lg'];

storiesOf('IconAndLabel', {})
    .addDecorator(centered)
    .add('IconAndLabel', () => (
        <IconAndLabel
            icon={<Icon
                name={knobs.select('icon', icons, 'csweb_information')}
                size={knobs.select('size', sizes)}/>}
            label={knobs.text('label', 'Information')}
        />))
    .add('IconAndLabel without Icon', () => (
        <div>
            <p>This text is left aligned</p>
            <IconAndLabel
               icon={null}
               label={knobs.text('label', 'Information')}
               allowEmptyIcon={knobs.boolean('allowEmptyIcon')}/>
        </div>))
    .add('SVGIconAndLabel', () => (
        <SVGIconAndLabel
            name={knobs.select('name', icons, 'csweb_history')}
            label={knobs.text('label', 'Verlauf')}
            size={knobs.select('size', sizes)}
        />))
    .add('SVGIconAndLink', () => (
        <SVGIconAndLink
            to="http://localhost"
            name = {knobs.select('name', icons, 'csweb_dashboard')}
            label= {knobs.text('label', 'Mein Dashboard')}
            size = {knobs.select('size', sizes)}
        />))
;
