import React from 'react';
import {storiesOf, knobs, centered} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {InfoBox} from 'cs-web-components-base';
import {icons} from './util.jsx';

storiesOf('InfoBox', {})
    .addDecorator(centered)
    .addWithInfo('default', () => {
        const options = {
            info: 'info',
            warming: 'warning',
            danger: 'danger',
            None: null
        };
        return (
            <InfoBox
                title={knobs.text("title", "Your Title")}
                msg={knobs.text("msg", "Your Message")}
                alertType={knobs.select("alertType", options, "info")}
                iconName={knobs.select("iconName", icons, "csapp_error_protocol")}/>
        );
    });
