import React from 'react';
import {storiesOf} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {Dialog} from 'cs-web-components-base';


class InputDialogStory extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: ""
        };
        this.onSelect = this.onSelect.bind(this);
    }

    onSelect(value) {
        this.setState({value});
    }

    render() {
        return (
            <Dialog.InputDialog
                title="InputDialog"
                ActionButton={
                    <Dialog.Buttons.Rename
                        title="ActionButton" />
                }
                isValueValid={Dialog.InputDialog.NO_EMPTY_VALUE}
                size={Dialog.SIZE_SMALL}
                onHide={() => {}}
                onSubmitValue={() => {}}
                value={this.state.value}>
                {"Input"}
            </Dialog.InputDialog>
        );
    }
}


storiesOf('InputDialog', {})
    .addWithInfo('default', () => {
        return (
            <InputDialogStory />
        );
    });
