import React from 'react';
import {storiesOf, knobs, centered} from 'cs-web-components-storybook';
// DO NOT import components use relative path! Only use Registry or
// from component libs(defined externals)
import {PersistentCollapsible} from 'cs-web-components-base';

storiesOf('PersistentCollapsible', {})
    .addDecorator(centered)
    .addWithInfo('default', () => {
        const defaultText = ["First Text", "Second Text", "Third Text"];
        return (
            <PersistentCollapsible
                header= {knobs.text("header", "Header")}
                onCollapsed= {() => {}}
                collapsedKey= "collapsedKey"
                useGlyphiconStyle= {true}>
                {knobs.array("children", defaultText).map((item, index) => (
                    <p key={index}>{item}</p>
                ))}
            </PersistentCollapsible>
        );
    });
