import React from 'react';
import {storiesOf, knobs, centered} from 'cs-web-components-storybook';
import {ProgressBar} from 'cs-web-components-base';

storiesOf('ProgressBar', {})
    .addDecorator(centered)
    .addWithInfo('ProgressBar', () => (
        <div style = {{width: '200px'}}>
            <ProgressBar
                value={knobs.number('value', 25)}
                max={knobs.number('max', 100)}
                hideLabel = {knobs.boolean('hideLabel')}/>
        </div>
    ));
