
import React from 'react';
import {storiesOf, centered} from 'cs-web-components-storybook';
import {ProgressDetail} from 'cs-web-components-base';

storiesOf('ProgressDetail', {})
    .addDecorator(centered)
    .add('default', () => (
        <ProgressDetail.ProgressDetailContainer>
            <ProgressDetail.ProgressDetailItem
                key="idle"
                state={ProgressDetail.ITEM_INCOMPLETE}
                label={'Idle State'}/>
            <ProgressDetail.ProgressDetailItem
                key="work"
                state={ProgressDetail.ITEM_IN_PROGRESS}
                label={'Working State'}/>
            <ProgressDetail.ProgressDetailItem
                key="done"
                state={ProgressDetail.ITEM_COMPLETE}
                label={'Completed State'}/>
        </ProgressDetail.ProgressDetailContainer>
    ));
